import {ComponentFixture, TestBed, tick} from '@angular/core/testing';
import {ReactiveFormsModule} from '@angular/forms';
import {UploadPhotoComponent} from './upload-photo.component';
import {PhotoService} from '../../services/photo.service';
import {RouterTestingModule} from '@angular/router/testing';
import {EventService} from '../../services/event.service';
import {BsModalRef} from 'ngx-bootstrap/modal';
import {of} from 'rxjs';
import {HttpClientTestingModule} from '@angular/common/http/testing';

describe('UploadPhotoComponent', () => {
  let component: UploadPhotoComponent;
  let fixture: ComponentFixture<UploadPhotoComponent>;
  let photoService: PhotoService;
  let eventService: EventService;

  beforeEach(async () => {
    await TestBed.configureTestingModule({
      imports: [ReactiveFormsModule, RouterTestingModule, HttpClientTestingModule],
      declarations: [UploadPhotoComponent],
      providers: [PhotoService, EventService, BsModalRef]
    }).compileComponents();
  });

  beforeEach(() => {
    fixture = TestBed.createComponent(UploadPhotoComponent);
    component = fixture.componentInstance;
    photoService = TestBed.inject(PhotoService);
    eventService = TestBed.inject(EventService);
    fixture.detectChanges();
  });

  it('Debe crear el componente', () => {
    expect(component).toBeTruthy();
  });

  it('Debe llamar a showAlertError() cuando el formulario no es válido o falta la foto', () => {
    spyOn(component, 'showAlertError');
    component.onSubmit();
    expect(component.showAlertError).toHaveBeenCalled();
  });

  it('Debe llamar al método create() y mostrar showAlertSuccessful() cuando el formulario es válido y la foto está presente', () => {
    spyOn(photoService, 'create').and.returnValue(of(null));
    spyOn(component, 'showAlertSuccessful');
    spyOn(eventService.photosUpdated, 'next');
    spyOn(component.bsModalRef, 'hide');

    const fakePhoto = {
      title: 'Prueba foto',
      description: 'Esto es una prueba de un test',
      photo: ''
    };

    component.newPhotoForm.controls['title'].setValue(fakePhoto.title);
    component.newPhotoForm.controls['description'].setValue(fakePhoto.description);
    component.base64textString = fakePhoto.photo;
    component.newPhoto = fakePhoto.photo;

    component.onSubmit();

    photoService.create(fakePhoto).subscribe(() => {
      expect(component.showAlertSuccessful).toHaveBeenCalled();
      expect(eventService.photosUpdated.next).toHaveBeenCalledWith('');
      expect(component.bsModalRef.hide).toHaveBeenCalled();
    });
  });

  it('Debe llamar a showAlertError() cuando se llama a getFile() con un tipo de archivo no válido', () => {
    spyOn(component, 'showAlertError');

    const event = {
      target: {
        files: [
          {
            type: 'text/plain'
          }
        ]
      }
    };

    component.getFile(event);

    expect(component.showAlertError).toHaveBeenCalled();
    expect(component.newPhoto).toBeNull();
  });

});
